define('ns_stepper/lyt-stepperOrchestrator',[
	'jquery',
	'underscore',
	'backbone',
	'marionette',
	'sweetAlert',
	'ns_stepper/lyt-step',
], function($, _, Backbone, Marionette, swal, LS) {

	'use strict';

	return Marionette.LayoutView.extend({
		/*===================================================
		=            Layout Stepper Orchestrator            =
		===================================================*/
		template: 'app/ns_modules/ns_stepper/tpl-stepperOrchestrator.html',
		className : 'ns-full-height orchestrator',

		events: {
			'click #infos' : 'infos',
			'click #btnNext' : 'nextStep',
			'click #btnPrev' : 'prevStep',
			'click #reset' : 'reset',
			'keyup input:not(:checkbox,:radio)' : 'datachanged_text',
			'change input:not(:checkbox,:radio)' : 'datachanged_text',
			'change input:checkbox' : 'datachanged_checkbox',
			'change input:radio' : 'datachanged_radio',
			'change input:file' : 'datachanged_file',
			'change select' : 'datachanged_select',
			'click #step-nav li' : 'changeStep',
			//'click .finished': 'finish'
		},

		regions: {
			StepManager: '#StepManager',
			step_content: '#step-content',
			actions: '#actions',
		},

		currentStep:0,

		onDestroy: function(){

		},

		initialize: function(options){

			this.steps=options.steps;
			var current;
			for(var i=0; i < this.steps.length; i++){
				current=this.steps[i];
				current.parent = this;
			}
			this.model=options.model;
			this.listenTo(this.model,'change', this.modelChanged);
			this.keyboard();
			
		},


		onShow: function(){
			this.initNavSteps();
			this.toStep(0);
			
		},

		onRender: function(){

		},

		initNavSteps: function(){
			for (var i = 0; i < this.steps.length; i++) {
				this.$el.find('#step-nav').append('<li class="step-item" id="'+this.steps[i].name+'"  disabled=disabled><span class="badge">'+(i+1)+'</span><span class="hidden-xs">'+this.steps[i].name+'</span><span class="chevron"></span></li>');
			};
		},

		modelChanged:function(){
			this.check();
			this.resetFromStep();
		},

		resetFromStep:function(){
			for (var i=this.currentStep+1; i < this.steps.length; i++){
				this.steps[i].reset();
			}
		},

		keyboard: function(){
			var ctx=this;
			$(document).keydown(function(e) {

				switch(e.which) {
					case 37: // left
						if(e.ctrlKey)
						ctx.prevStep();
					break;

					case 38: // up
					break;

					case 39: // right
						if(e.ctrlKey)
						ctx.nextStep();
					break;

					case 40: // down
					break;

					default: return; // exit this handler for other keys
				}
				//e.preventDefault(); // prevent the default action (scroll / move caret)
			});
		},

		/*==========  Next / Prev  ==========*/

		nextStep: function(){
			if(this.steps[this.currentStep].nextOK()) {
				if (this.currentStep >= this.steps.length-1) {
				this.finish();

				} else {
					this.currentStep++;
					this.toStep(this.currentStep);
				}
				
			}
		},

		prevStep: function(){
			this.currentStep === 0 ? this.currentStep : this.currentStep--;
			this.toStep(this.currentStep);
		},
		

		toStep: function(numstep){

			this.currentStep = numstep;
			this.step_content.show( this.steps[this.currentStep], {preventDestroy: true} );
			this.check();
			this.styleNav();


			if (this.currentStep==this.steps.length-1){

				//this.$el.find('#btnNext').attr( 'disabled', 'disabled');
				this.$el.find('#btnNext').addClass('finished').find( 'span'
					).html('Finish').parent().find('.icon').removeClass('rightarrow').addClass('validate');

			}
			else {
				 this.$el.find('#btnNext').removeClass('finished').find( 'span'
					).html('Next').parent().find('.icon').removeClass('validate').addClass('rightarrow');
			}

			this.displayPrev(this.currentStep);
			


		},

		check: function(){

			if(this.steps[this.currentStep].validate()) {
				this.$el.find('#btnNext').removeAttr('disabled').show();                
			}
			else{
				/*this.$el.find('#btnNext').attr( 'disabled', 'disabled' );*/
				this.$el.find('#btnNext').hide();
			}
		},


		/*==========  Style Nav Steps  ==========*/
		displayPrev: function() {
			if (this.currentStep==0){
				/* this.$el.find('#btnPrev').attr( 'disabled', 'disabled');*/
				this.$el.find('#btnPrev').hide();
 
			}
			else {
				/*  this.$el.find('#btnPrev').removeAttr('disabled'); */
				this.$el.find('#btnPrev').show();
			}

		},

		styleNav: function(){
			this.$el.find('#step-nav li.step-item.active').removeClass('active');
			this.$el.find('#step-nav li#'+this.steps[this.currentStep].name).addClass('active');
			for (var i = 0; i < this.currentStep; i++) {
				this.$el.find('#step-nav li#'+this.steps[i].name).addClass('complete');
			};
			for (var i = this.currentStep; i < this.steps.length; i++) {
				this.$el.find('#step-nav li#'+this.steps[i].name).removeClass('complete');
			};
		},


		GetStepByName: function(StepName){
			   for (var i=0; i < this.steps.length; i++){
				if (this.steps[i].name == StepName ) return this.steps[i].Name ;
			}  
			return null;
		},

		
		datachanged_text: function(e){
			this.steps[this.currentStep].datachanged_text(e);
		},

		datachanged_checkbox: function(e){
			this.steps[this.currentStep].datachanged_checkbox(e);
		},


		datachanged_radio: function(e){
			this.steps[this.currentStep].datachanged_radio(e);
		},

		datachanged_file : function(e){
			this.steps[this.currentStep].datachanged_file(e);
		},
		datachanged_select: function(e){
			this.steps[this.currentStep].datachanged_select(e);
		},
		infos: function(){
			console.info(this.model);
		},

		changeStep : function(e){

			var element = $(e.target);
			var idStep ;
			if ( element.is( "li" )) {
				idStep = parseInt($(e.target).find('span.badge').text())-1 ;
			} else {
				idStep = parseInt($(e.target).parent().find('span.badge').text())-1 ;
			}
			
			if (this.currentStep >= idStep)
				this.toStep(idStep);
		},

		alert_end: function() {

			var self = this;
			swal({

				title: " Well done ! ",
				text: "",
				type: "success",
				showCancelButton: true,
				confirmButtonColor: "green",
				confirmButtonText: "Redo",
				cancelButtonText: "Finish",
				closeOnConfirm: true,
				closeOnCancel: true
				},
				function(isConfirm){
					if (isConfirm) {
						 self.toStep(0);
					} else {
						
					}
			});
		},

		finish: function() {
			this.alert_end();
		}

	});

});

define('modules/input/layouts/lyt-stepOrchestrator',[
	'ns_stepper/lyt-stepperOrchestrator',
	//'stepper/lyt-stepperOrchestrator',

], function(StepperOrchestrator) {

	'use strict';

	return StepperOrchestrator.extend({
		
		/*==========  Next / Prev  ==========*/
		onShow: function(){
			StepperOrchestrator.prototype.onShow.apply(this, arguments);
		},
		nextStep: function(){
			var currentStep = this.steps[this.currentStep];
			if(currentStep.nextOK()) {
				this.currentStep++;
				if (this.currentStep== this.steps.length) { this.finish(); }
				else {this.toStep(this.currentStep); }
			}
		},

		prevStep: function(){
			if(this.currentStep === (this.steps.length - 1)){
				this.$el.find('#btnNext').find( 'span').text('Next');
			}
			this.currentStep === 0 ? this.currentStep : this.currentStep--;
			this.toStep(this.currentStep);
		},


	});

});

//radio
define('modules/input/layouts/lyt-step1',[
	'jquery',
	'underscore',
	'backbone',
	'marionette',

	'ns_stepper/lyt-step',

], function($, _, Backbone, Marionette, Step) {

	'use strict';

	 return Step.extend({
		/*===================================================
		=            Layout Stepper Orchestrator            =
		===================================================*/
		events:{
			'click .manual-tile' : 'selectMode'
		},


		datachanged_radio: function(e){
			var target= $(e.target);
			var val=$(target).attr('value');
			this.updateModel(val);
		},
		selectMode : function(e){
			console.log('passed');
			$('div.manual-tile').removeClass('select');
			var radioSelection =  $(e.target).hasClass('radio-select');
			if (radioSelection){
				$(e.target).addClass('select');
				$('input[name="stationtype"]').each(function() {
					$(this).removeAttr('checked').prop('checked',false);
				});
				var radioElements = $(e.target).find('input[type="radio"]');
				var radio =  $(radioElements)[0];
				$(radio).attr('checked','checked').prop('checked',true);
				var val = $(radio).attr('value');
				this.updateModel(val);
			}
		}, 
		updateModel : function(value){
			console.log('Change Model')
			this.model.set('start_stationtype' , value);
			for(var key in this.model.attributes) {
				if(key != 'start_stationtype'){
					this.model.set(key,null);
				}
			}
		},

	});
});

